import React, { useState } from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';

function MultiSelectDropdown() {
  const [selectedServices, setSelectedServices] = useState([]);

  const services = [
    { id: 1, label: "Be a Mentor" },
    { id: 2, label: "As a Speaker" },
    { id: 3, label: "Wish to Recruit" }
  ];

  const handleSelect = (service) => {
    const isServiceSelected = selectedServices.includes(service);
    if (isServiceSelected) {
      setSelectedServices(selectedServices.filter(s => s !== service));
    } else {
      setSelectedServices([...selectedServices, service]);
    }
  };

  const renderButtonText = () => {
    return selectedServices.length > 0
      ? selectedServices.join(', ')
      : "Select Service";
  };

  return (
    <div className="dropdown">
      <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-bs-toggle="dropdown" aria-expanded="true">
        {renderButtonText()}
      </button>
      <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton">
        {services.map((service) => (
          <li key={service.id} onClick={() => handleSelect(service.label)}>
            <label className="dropdown-item">
              <input
                type="checkbox"
                checked={selectedServices.includes(service.label)}
                onChange={() => handleSelect(service.label)}
              /> {service.label}
            </label>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default MultiSelectDropdown;
