import React, { useState, useEffect } from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';

function JobSection() {
  // State to hold job data
  const [jobs, setJobs] = useState([]);

  // Simulating fetching data from an API or admin post
  useEffect(() => {
    // Example job data
    const fetchedJobs = [
      {
        id: 1,
        title: 'PHP Developer',
        company: 'Company 1',
        location: 'Location 1',
        area: 'IT/ Management',
        skills: 'PHP, HTML, CSS, Bootstrap',
      },
      {
        id: 2,
        title: 'PHP Developer',
        company: 'Company 1',
        location: 'Location 1',
        area: 'IT/ Management/Digital Marketing',
        skills: 'PHP, HTML, CSS, Bootstrap',
      },
      // Add more jobs as needed
    ];

    // Update state with fetched job data
    setJobs(fetchedJobs);
  }, []);

  return (
    
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2>Job Section</h2>
        <button className="btn btn-warning">Post a New Job</button>
      </div>
      
      <div className="mb-3">
        <input 
          type="text" 
          className="form-control" 
          placeholder="Type a Job Role, Location, Domain" 
        />
        <button className="btn btn-primary mt-2">Search</button>
      </div>

      <div className="mb-3">
        <button className="btn btn-light">My Jobs</button>
      </div>

      {/* Rendering job cards dynamically */}
      {jobs.map((job) => (
        <div className="card mb-3" key={job.id}>
          <div className="card-body">
            <h5 className="card-title">{job.title}</h5>
            <p className="card-text">
              <strong>Company :</strong> {job.company}<br/>
              <strong>Job Location :</strong> {job.location}<br/>
              <strong>Job Area :</strong> {job.area}<br/>
              <strong>Skills :</strong> {job.skills}
            </p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default JobSection;
