import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { login } from '../services/admin';
import './Login.css'; // Create a CSS file for custom styling

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isEmailEmpty, setEmailEmpty] = useState(false);
  const [isPasswordEmpty, setPasswordEmpty] = useState(false);

  // get navigation hook
  const navigate = useNavigate();

  const onLogin = async () => {
    if (email.length === 0) {
      toast.error('Please enter email');
    } else if (password.length === 0) {
      toast.error('Please enter password');
    } else {
      // call login API and check its success
      toast.success('Successfully logged In');
      navigate('/home');
      // Uncomment the below code to handle actual login
      // const result = await login(email, password);
      // if (result['status'] === 'success') {
      //   const data = result['data'];

      //   // persist the token and user name in session storage
      //   sessionStorage['name'] = data['name'];
      //   sessionStorage['token'] = data['token'];

      //   // go to home screen
      //   navigate('/home');
      // } else {
      //   toast.error(result['error']);
      // }
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <img src="your-logo.png" alt="Logo" className="login-logo" /> {/* Update with your logo path */}
          <h2 className="login-title">Welcome back, Login.</h2>
        </div>
        <div className="login-body">
          <div className="mb-3">
            <label>Email</label>
            <input
              onChange={(e) => {
                if (e.target.value.length === 0) {
                  setEmailEmpty(true);
                } else {
                  setEmailEmpty(false);
                }
                setEmail(e.target.value);
              }}
              type="email"
              className="form-control"
              placeholder="ken@gmail.com"
            />
            {isEmailEmpty && (
              <p style={{ color: 'red' }}>Email is mandatory</p>
            )}
          </div>
          <div className="mb-3">
            <label>Password</label>
            <input
              onChange={(e) => {
                if (e.target.value.length === 0) {
                  setPasswordEmpty(true);
                } else {
                  setPasswordEmpty(false);
                }
                setPassword(e.target.value);
              }}
              type="password"
              className="form-control"
              placeholder="kenspassword"
            />
            {isPasswordEmpty && (
              <p style={{ color: 'red' }}>Password is mandatory</p>
            )}
          </div>
          <button onClick={onLogin} className="btn btn-primary login-button">
            Login
          </button>
          <div className="login-footer">
            Don't have an account? <Link to="/register">Sign up</Link>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
