import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import Navbar from '../components/navbar';
import Footer from '../components/footerComponent';

function JobDescriptionPage() {
    const { id } = useParams(); // Get job ID from URL parameters
    const [jobDetails, setJobDetails] = useState(null);

    useEffect(() => {
        // Fetch job details from backend using the job ID
        const fetchJobDetails = async () => {
            // Replace with your backend API call
            const response = await fetch(`/api/jobs/${id}`);
            const data = await response.json();
            setJobDetails(data);
        };

        fetchJobDetails();
    }, [id]);

    if (!jobDetails) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <Navbar />
            <div className="container mt-5">
                <div className="card">
                    <div className="card-header d-flex justify-content-between align-items-center" style={{ backgroundColor: '#B4D4FF' }}>
                        <h3>{jobDetails.jobRole}</h3>
                        <Link to="/jobsection" className="btn btn-secondary">Back to all Jobs</Link>
                    </div>
                    <div className="card-body" style={{ backgroundColor: '#EEF5FF' }}>
                        <table className="table">
                            <tbody>
                                <tr>
                                    <th scope="row">Job Role:</th>
                                    <td>{jobDetails.jobRole}</td>
                                </tr>
                                <tr>
                                    <th scope="row">Experience Level:</th>
                                    <td>{jobDetails.experienceLevel}</td>
                                </tr>
                                <tr>
                                    <th scope="row">Job Location:</th>
                                    <td>{jobDetails.jobLocation}</td>
                                </tr>
                                <tr>
                                    <th scope="row">Domain Tag:</th>
                                    <td>{jobDetails.domainTag}</td>
                                </tr>
                                <tr>
                                    <th scope="row">Job Posting Date:</th>
                                    <td>{jobDetails.postingDate}</td>
                                </tr>
                                <tr>
                                    <th scope="row">Job Application End Date:</th>
                                    <td>{jobDetails.applicationEndDate}</td>
                                </tr>
                            </tbody>
                        </table>

                        <div className="mt-4">
                            <h5>Job Description</h5>
                            <p>{jobDetails.jobDescription}</p>
                        </div>
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default JobDescriptionPage;
