import Navbar from "../components/navbar";

import React, { useState } from 'react';
// import 'bootstrap/dist/css/bootstrap.min.css';
import Footer from "../components/footerComponent";

function JobPostingPage() {
  const [jobDetails, setJobDetails] = useState({
    id: '',
    companyName: '',
    jobRole: '',
    jobDescription: '',
    jobLocation: '',
    experienceLevel: '',
    workMode: 'Work from Office', // default value
    salaryPackage: 0, // default value
    jobDomain: '',
    jobPostingDate: '',
    jobApplicationEndDate: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setJobDetails({
      ...jobDetails,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the form submission logic here (e.g., API call to save job details)
    console.log('Job Details:', jobDetails);
  };

  return (
    <div>
        <Navbar/>
    <div className="container mt-4">
      <h2>Post a New Job</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="id" className="form-label">Enter your ID</label>
          <input 
            type="number" 
            className="form-control" 
            id="id" 
            name="id" 
            value={jobDetails.id}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          />
        </div>
        
        <div className="mb-3">
          <label htmlFor="companyName" className="form-label">Company Name</label>
          <input 
            type="text" 
            className="form-control" 
            id="companyName" 
            name="companyName" 
            value={jobDetails.companyName}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="jobRole" className="form-label">Job Role</label>
          <input 
            type="text" 
            className="form-control" 
            id="jobRole" 
            name="jobRole" 
            value={jobDetails.jobRole}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="jobDescription" className="form-label">Job Description</label>
          <textarea 
            className="form-control" 
            id="jobDescription" 
            name="jobDescription" 
            rows="4"
            value={jobDetails.jobDescription}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="jobLocation" className="form-label">Job Location</label>
          <input 
            type="text" 
            className="form-control" 
            id="jobLocation" 
            name="jobLocation" 
            value={jobDetails.jobLocation}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="experienceLevel" className="form-label">Experience Level (in years)</label>
          <input 
            type="number" 
            className="form-control" 
            id="experienceLevel" 
            name="experienceLevel" 
            value={jobDetails.experienceLevel}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="workMode" className="form-label">Work Mode</label>
          <select 
            className="form-control" 
            id="workMode" 
            name="workMode"
            value={jobDetails.workMode}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          >
            <option>Work from Office</option>
            <option>Work from Home</option>
            <option>Hybrid mode</option>
          </select>
        </div>

        <div className="mb-3">
          <label htmlFor="salaryPackage" className="form-label">Salary Package (default is 0)</label>
          <input 
            type="number" 
            className="form-control" 
            id="salaryPackage" 
            name="salaryPackage" 
            value={jobDetails.salaryPackage}
            onChange={handleChange}
            style={{ width: '300px' }}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="jobDomain" className="form-label">Job Domain</label>
          <input 
            type="text" 
            className="form-control" 
            id="jobDomain" 
            name="jobDomain" 
            value={jobDetails.jobDomain}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="jobPostingDate" className="form-label">Job Posting Date</label>
          <input 
            type="date" 
            className="form-control" 
            id="jobPostingDate" 
            name="jobPostingDate" 
            value={jobDetails.jobPostingDate}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          />
        </div>

        <div className="mb-3">
          <label htmlFor="jobApplicationEndDate" className="form-label">Job Application End Date</label>
          <input 
            type="date" 
            className="form-control" 
            id="jobApplicationEndDate" 
            name="jobApplicationEndDate" 
            value={jobDetails.jobApplicationEndDate}
            onChange={handleChange}
            required
            style={{ width: '300px' }}
          />
        </div>

        <button type="submit" className="btn btn-success">Submit Job Posting</button>
      </form>
    </div>
    <Footer/>
    </div>
  );
}

export default JobPostingPage;
