import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

function JobPostingPage() {
    const [id, setId] = useState('');
    const [companyName, setCompanyName] = useState('');
    const [jobRole, setJobRole] = useState('');
    const [jobDescription, setJobDescription] = useState('');
    const [jobLocation, setJobLocation] = useState('');
    const [experienceLevel, setExperienceLevel] = useState('');
    const [workMode, setWorkMode] = useState('Work from Office');
    const [salaryPackage, setSalaryPackage] = useState(0);
    const [jobDomain, setJobDomain] = useState('');
    const [jobPostingDate, setJobPostingDate] = useState('');
    const [jobApplicationEndDate, setJobApplicationEndDate] = useState('');

    const navigate = useNavigate();

    const onSubmitJob = async () => {
        if (id.length === 0) {
            toast.error('Please enter your ID');
        } else if (companyName.length === 0) {
            toast.error('Please enter company name');
        } else if (jobRole.length === 0) {
            toast.error('Please enter job role');
        } else if (jobDescription.length === 0) {
            toast.error('Please enter job description');
        } else if (jobLocation.length === 0) {
            toast.error('Please enter job location');
        } else if (experienceLevel.length === 0) {
            toast.error('Please enter experience level');
        } else if (jobDomain.length === 0) {
            toast.error('Please enter job domain');
        } else if (jobPostingDate.length === 0) {
            toast.error('Please enter job posting date');
        } else if (jobApplicationEndDate.length === 0) {
            toast.error('Please enter job application end date');
        } else {
            // Assuming the job posting service is successful
            console.log('Job details submitted:', {
                id, companyName, jobRole, jobDescription,
                jobLocation, experienceLevel, workMode,
                salaryPackage, jobDomain, jobPostingDate, jobApplicationEndDate,
            });
            toast.success('Job successfully posted');
            navigate('/jobs');  // Redirect to jobs list page or any other page
        }
    };

    return (
        <div className="container mt-4">
            <h2 className="page-header mb-4">Post a New Job</h2>
            <div className="row">
                <div className="col"></div>
                <div className="col">
                    <div className="form">
                        <div className="mb-3">
                            <label htmlFor="id" className="form-label">Enter your ID</label>
                            <input
                                onChange={(e) => setId(e.target.value)}
                                type="number"
                                className="form-control"
                                placeholder="Enter your ID"
                                value={id}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="companyName" className="form-label">Company Name</label>
                            <input
                                onChange={(e) => setCompanyName(e.target.value)}
                                type="text"
                                className="form-control"
                                placeholder="Enter company name"
                                value={companyName}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="jobRole" className="form-label">Job Role</label>
                            <input
                                onChange={(e) => setJobRole(e.target.value)}
                                type="text"
                                className="form-control"
                                placeholder="Enter job role"
                                value={jobRole}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="jobDescription" className="form-label">Job Description</label>
                            <textarea
                                onChange={(e) => setJobDescription(e.target.value)}
                                className="form-control"
                                placeholder="Enter job description"
                                value={jobDescription}
                                rows="4"
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="jobLocation" className="form-label">Job Location</label>
                            <input
                                onChange={(e) => setJobLocation(e.target.value)}
                                type="text"
                                className="form-control"
                                placeholder="Enter job location"
                                value={jobLocation}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="experienceLevel" className="form-label">Experience Level (in years)</label>
                            <input
                                onChange={(e) => setExperienceLevel(e.target.value)}
                                type="number"
                                className="form-control"
                                placeholder="Enter experience level"
                                value={experienceLevel}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="workMode" className="form-label">Work Mode</label>
                            <select
                                onChange={(e) => setWorkMode(e.target.value)}
                                className="form-control"
                                value={workMode}
                            >
                                <option>Work from Office</option>
                                <option>Work from Home</option>
                                <option>Hybrid mode</option>
                            </select>
                        </div>

                        <div className="mb-3">
                            <label htmlFor="salaryPackage" className="form-label">Salary Package (default is 0)</label>
                            <input
                                onChange={(e) => setSalaryPackage(e.target.value)}
                                type="number"
                                className="form-control"
                                placeholder="Enter salary package"
                                value={salaryPackage}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="jobDomain" className="form-label">Job Domain</label>
                            <input
                                onChange={(e) => setJobDomain(e.target.value)}
                                type="text"
                                className="form-control"
                                placeholder="Enter job domain"
                                value={jobDomain}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="jobPostingDate" className="form-label">Job Posting Date</label>
                            <input
                                onChange={(e) => setJobPostingDate(e.target.value)}
                                type="date"
                                className="form-control"
                                value={jobPostingDate}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="jobApplicationEndDate" className="form-label">Job Application End Date</label>
                            <input
                                onChange={(e) => setJobApplicationEndDate(e.target.value)}
                                type="date"
                                className="form-control"
                                value={jobApplicationEndDate}
                            />
                        </div>

                        <div className="d-flex justify-content-between">
                            <button onClick={() => navigate(-1)} className="btn btn-secondary">Back</button>
                            <button onClick={onSubmitJob} className="btn btn-success">Submit Job Posting</button>
                        </div>
                    </div>
                </div>
                <div className="col"></div>
            </div>
        </div>
    );
}

export default JobPostingPage;
